sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here

var num = 'Number/s: ';

if(current.variables.one == "true") {
	num += 'One';
}
if(current.variables.two == "true") {
	num += ', Two';
}
if(current.variables.three == "true") {
	num += ', Three';
}

if(num.charAt(10)==',')	{ //remove comma if it the fist character.
	num = num.replace(', ', '');
}	


template.print(num + "<br /><br />" + num);


})(current, template, email, email_action, event);
